import React, { useState, useEffect, useRef } from 'react';
import Header from "./header";
import About2 from "./about2";
import Details from "./details";
import About from "./about";
import Details2 from "./details2";
import Header2 from "./header2";
import CookiePolicyPopup from "./CookiePolicyPopup";
import IndexPage from "./IndexPage";
import SplashScreen from "./SplashScreen";
import Popup from "./Popup";
import Categorii from "./categorii";
import Footer2 from "./footer2";
import './fade-in.css'; // import the fade-in class

function Home() {
  const [showPopup, setShowPopup] = useState(false);
  const handleScroll = () => {
    if (window.pageYOffset > 1600) {
      setShowPopup(true);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Add a ref to each component
  const about2Ref = useRef(null);
  const categoriiRef = useRef(null);
  const aboutRef = useRef(null);
  const details2Ref = useRef(null);
  const header2Ref = useRef(null);
  const footer2Ref = useRef(null);

  // Use the IntersectionObserver API to check if each component is in the viewport
  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      if (entry.isIntersecting) {
        entry.target.classList.add('visible');
        observer.unobserve(entry.target);
      }
    }, { threshold: 0.5 });

    if (about2Ref.current) {
      observer.observe(about2Ref.current);
    }
    if (categoriiRef.current) {
      observer.observe(categoriiRef.current);
    }
    if (aboutRef.current) {
      observer.observe(aboutRef.current);
    }
    if (details2Ref.current) {
      observer.observe(details2Ref.current);
    }
    if (header2Ref.current) {
      observer.observe(header2Ref.current);
    }
    if (footer2Ref.current) {
      observer.observe(footer2Ref.current);
    }

    return () => {
      observer.disconnect();
    };
  }, []);

  return (
    <div>
      {showPopup && <Popup />}
      <Header/>
      <About2 ref={about2Ref} className="fade-in" />
      <Categorii ref={categoriiRef} className="fade-in" />
      <About ref={aboutRef} className="fade-in" /> 
<Details2 ref={details2ref} className="fade-in" />
<Header2 ref={headerref} className="fade-in" />
<Footer2 ref={ref} className="fade-in" />
    </div>
  );
};

export default Home;
