import { useEffect, useState } from "react";
import axios from "axios";
import "./IndexPage.css"
import { Link } from "react-router-dom";
import Image from "./image";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBox, faCalendarAlt,faRoad } from '@fortawesome/free-solid-svg-icons';




export default function Details() {
  const [places, setPlaces] = useState([]);

  useEffect(() => {
    const fetchData = () => {
      axios.get("/places").then(response => { 
        setPlaces(response.data);
      });
    };
    fetchData(); // Fetch data initially
    const intervalId = setInterval(fetchData, 10 * 60 * 1000); // Fetch data every 10 minutes
    return () => clearInterval(intervalId); // Cleanup function to clear the interval
  }, []);
 
  const [selectedMarca, setSelectedMarca] = useState("");
  const [selectedModel, setSelectedModel] = useState("");
  

  
    const handleButtonClick = () => {
      window.location.href = '/IndexPage?marca=Legume';
    };

  const shuffleArray = (array) => {
    let currentIndex = array.length,  randomIndex;
    while (currentIndex !== 0) {
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex--;
      [array[currentIndex], array[randomIndex]] = [
        array[randomIndex], array[currentIndex]];
    }
    return array;
  };

  const limitedPlaces = shuffleArray(places).slice(0, 3); // display 3 random posts

  return(
    <div className="main2"> 
      <div className="container">
        <div className="details container">
        <div className="row row-cols-sm-1 row-cols-md-2 row-cols-lg-3 g-4">
        <div className="col ">
                  <div className="box card-body p-0  shadow-sm mb-5">
        <div className="marca-buttons">
        <button
  onClick={handleButtonClick}
  className={`marca-button ${selectedMarca === 'Fructe' ? 'active' : ''}`}
>
  Fructe
</button>
  
  </div>
  </div>


  <div className="col ">
                  <div className="box card-body p-0  shadow-sm mb-5">
        <div className="marca-buttons">
  
  <button
    onClick={() => setSelectedMarca('Legume')}
    className={`marca-button ${selectedMarca === 'Legume' ? 'active' : ''}`}
  >
    Legume
  </button>
  </div>
  </div>

  <div className="col ">
                  <div className="box card-body p-0  shadow-sm mb-5">
        <div className="marca-buttons">
  
  <button
    onClick={() => setSelectedMarca('Mezeluri')}
    className={`marca-button ${selectedMarca === 'Mezeluri' ? 'active' : ''}`}
  >
    Mezeluri
  </button>
  
  </div>
  </div>
  </div>
  </div>
  </div>
          </div>
        </div>
      </div>
    </div>
  );
};
