import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import cos from "./images/cos.png";

const CartLink = () => {
  const [cartQuantity, setCartQuantity] = useState(0);

  useEffect(() => {
    // Retrieve the cart quantity from localStorage or any other data source
    const updatedCartQuantity = JSON.parse(localStorage.getItem("cart"))?.length || 0;
    setCartQuantity(updatedCartQuantity);
  }, []);

  useEffect(() => {
    const handleStorageChange = (event) => {
      if (event.key === "cart") {
        const updatedCartQuantity = JSON.parse(event.newValue)?.length || 0;
        setCartQuantity(updatedCartQuantity);
      }
    };

    // Listen for changes in the "cart" localStorage key
    window.addEventListener("storage", handleStorageChange);

    return () => {
      // Clean up the event listener
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []);

  return (
    <div className="navbar-left">
      <Link to="/CartPage" className="nav-link">
        <img src={cos} alt="cos" />
        <span className="cart-quantity">{cartQuantity}</span>
      </Link>
    </div>
  );
};

export default CartLink;
