import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import "./IndexPage.css";
import { Link } from "react-router-dom";
import Image from "./image";
import { UserContext } from "./UserContext";

function Fructe() {
  const [places, setPlaces] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const placesPerPage = 9;

  const { cart, setCart } = useContext(UserContext);

  useEffect(() => {
    axios.get("/places").then((response) => {
      const filteredPlaces = response.data.filter(
        (place) => place.marca === "Fructe"
      );
      setPlaces(filteredPlaces);
    });
  }, []);

  const lastPlaceIndex = currentPage * placesPerPage;
  const firstPlaceIndex = lastPlaceIndex - placesPerPage;
  const currentPlaces = places.slice(firstPlaceIndex, lastPlaceIndex);
  const totalPages = Math.ceil(places.length / placesPerPage);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
    if (window.pageYOffset > 0) {
      window.scrollTo(0, 0);
    }
  };

  const addToCart = (place) => {
    const updatedPlace = { ...place, quantity: 1 }; // Add a quantity property to the place object
    let updatedCart = localStorage.getItem("cart");
    if (!updatedCart) {
      updatedCart = [];
    } else {
      updatedCart = JSON.parse(updatedCart);
    }
  
    // Check if the place is already in the cart
    const placeIndex = updatedCart.findIndex(item => item._id === place._id);
    if (placeIndex !== -1) {
      // If the place is already in the cart, increase the quantity
      updatedCart[placeIndex].quantity += 1;
    } else {
      // If the place is not in the cart, add it with quantity 1
      updatedCart.push(updatedPlace);
    }
  
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };
  
  return (
    <>
      <div className="top"></div>
      <div className="main2">
        <div className="container">
          <div className="details container">
            <div className="row row-cols-sm-1 row-cols-md-2 row-cols-lg-3 g-4">
              {currentPlaces.length > 0 &&
                currentPlaces.map((place) => (
                  <Link
                    to={"/place/" + place._id}
                    key={place._id}
                    className="link-no-underline"
                  >
                    <div className="col">
                      <div className="box card-body p-0  shadow-sm mb-5">
                        {place.photos.length > 0 && (
                          <Image
                            src={place.photos[0]}
                            className="img-fluid"
                            style={{
                              height: "270px",
                              width: "100%",
                              objectFit: "cover",
                            }}
                          />
                        )}
                        <div className="box_content">
                          <h4>{place.title}</h4>
                          <div className="row pl-2 pr-2">
                            <div>{place.km}</div>
                            <button
                              style={{
                                background: "rgb(216 212 208 / 78%)",
                                color: "var(--main)",
                                padding: "14px",
                                width: "131px",
                                marginLeft: "115px",
                              }}
                          className="btn1"
                          onClick={() => addToCart(place)}
                        >
                          Adauga in cos
                        </button>
                        {place.quantity && ( // Render the quantity only if it exists
                          <div>
                            <button
                              onClick={() => {
                                addToCart(place);
                              }}
                              className="quantity-increase"
                            >
                              +
                            </button>
                            {place.quantity}
                            <button
                              onClick={() => {
                                // Decrease the quantity
                                // Check if the place is already in the cart
                                const placeIndex = cart.findIndex(
                                  (item) => item._id === place._id
                                );
                                if (placeIndex !== -1) {
                                  // If the place is in the cart, decrease the quantity
                                  if (cart[placeIndex].quantity > 1) {
                                    cart[placeIndex].quantity -= 1;
                                    setCart([...cart]);
                                    localStorage.setItem(
                                      "cart",
                                      JSON.stringify(cart)
                                    );
                                  } else {
                                    // If the quantity is 1, remove the place from the cart
                                    cart.splice(placeIndex, 1);
                                    setCart([...cart]);
                                    localStorage.setItem(
                                      "cart",
                                      JSON.stringify(cart)
                                    );
                                  }
                                }
                              }}
                              className="quantity-decrease"
                            >
                              -
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              </Link>
            ))}
        </div>
      </div>
    </div>
  </div>
</>
);
}

export default Fructe;
