import React, { useEffect, useState } from "react";
import axios from 'axios';
import { Link } from 'react-router-dom';

const PlaceSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showResults, setShowResults] = useState(false);

  useEffect(() => {
    fetchPlaces();
  }, []);

  const fetchPlaces = async () => {
    try {
      // Fetch places from your server-side API endpoint using Axios
      const response = await axios.get('/places');
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error fetching places:', error);
    }
  };

  const handleSearch = () => {
    if (searchTerm.trim() !== '') {
      const filteredPlaces = searchResults.filter(place =>
        hasThreeConsecutiveLetters(place.title.toLowerCase(), searchTerm.toLowerCase())
      );

      // Additional filtering when search term length is greater than 3
      if (searchTerm.length > 3) {
        const refinedResults = filteredPlaces.filter(place =>
          place.title.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setShowResults(true);
        return refinedResults;
      } else {
        setShowResults(true);
        return filteredPlaces;
      }
    } else {
      setShowResults(false);
      return [];
    }
  };

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const hasThreeConsecutiveLetters = (str, search) => {
    for (let i = 0; i <= str.length - 3; i++) {
      if (str.slice(i, i + 3) === search) {
        return true;
      }
    }
    return false;
  };

  const [places, setPlaces] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  // Rest of your code...

  // ...

  return (
    <div>
      <input
        type="text"
        placeholder="Search by title..."
        value={searchTerm}
        onChange={handleChange}
      />

      <div className="top"></div>
      <div className="main2">
        <div className="container">
          <div className="details container">
            <div className="row row-cols-sm-1 row-cols-md-2 row-cols-lg-3 g-4">
              {showResults && places.length > 0 &&
                places.map((place) => (
                  <div className="col" key={place._id}>
                    <div className="box card-body p-0 shadow-sm mb-5">
                      <Link to={"/place/" + place._id} key={place._id} className="link-no-underline">
                        {place.photos.length > 0 && (
                          <img
                            src={place.photos[0]}
                            className="img-fluid"
                            alt={place.title}
                            style={{
                              height: "270px",
                              width: "100%",
                              objectFit: "cover",
                            }}
                          />
                        )}
                      </Link>
                      <div className="box_content">
                        <h4>{place.title}</h4>
                        <div className="row pl-2 pr-2">
                          <div className="quantity-control">
                            {/* Rest of your code... */}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PlaceSearch;
