import "./navbar.css";
import React, { useState, useEffect, useContext } from "react";
import { Link, useLocation } from "react-router-dom";
import MenuImg from "./images/menu.png";
import { UserContext } from "./UserContext";

const NavBar = () => {
  const { user, cartItems } = useContext(UserContext);
  const [navbar, setNavbar] = useState(false);
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const isAddedToCart = queryParams.get("addedToCart");

  useEffect(() => {
    const changeBg = () => {
      if (window.scrollY >= 100) {
        setNavbar(true);
      } else {
        setNavbar(false);
      }
    };

    window.addEventListener("scroll", changeBg);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener("scroll", changeBg);
    };
  }, []);

  const cartItemCount = cartItems ? cartItems.length : 0;

  return (
    <>
      <nav
        className={
          navbar
            ? "navbar navbar-expand-lg navbar-expand-md fixed-top active"
            : "navbar navbar-expand-lg navbar-expand-md fixed-top"
        }
      >
        <a href="/" className="navbar-brand">
          <span>eco</span>Taran
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <img src={MenuImg} alt="menu" />
        </button>
        <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
          <div className="navbar-nav">
            <div className="nav-item">
              <a href="/" className="nav-link">
                Acasa
              </a>
            </div>
            <div className="nav-item">
              <a href="/IndexPage" className="nav-link">
                Anunturi
              </a>
            </div>
            {user ? (
              <div className="nav-item">
                <a href="/userpage" className="nav-link">
                  {user.name}
                </a>
              </div>
            ) : (
              <>
                <div className="nav-item">
                  <a href="/CartPage" className="nav-link">
                    Cart {cartItemCount > 0 && <span>({cartItemCount})</span>}
                  </a>
                </div>
                <div className="nav-item">
                  <a href="/login" className="nav-link">
                    Log In
                  </a>
                </div>
              </>
            )}
          </div>
        </div>
      </nav>
      {isAddedToCart && (
        <div className="added-to-cart-message">
          Item has been added to the cart!
        </div>
      )}
    </>
  );
};

export default NavBar;
