import React, { useState } from "react";

const CartPage = ({ cartItems }) => {
  const removeFromCart = (place) => {
    const updatedCartItems = cartItems.filter((cartItem) => cartItem._id !== place._id);
    setCartItems(updatedCartItems);
  };

  const calculateTotalPrice = () => {
    let total = 0;
    cartItems.forEach((place) => {
      total += place.price;
    });
    return total.toFixed(2);
  };

  return (
    <div>
      <h2>Cart Page</h2>
      {cartItems.length > 0 ? (
        <div>
          <h3>Items in Cart:</h3>
          <ul>
            {cartItems.map((place) => (
              <li key={place._id}>
                {place.title} - ${place.price.toFixed(2)}{" "}
                <button onClick={() => removeFromCart(place)}>Remove</button>
              </li>
            ))}
          </ul>
          <h3>Total Price: ${calculateTotalPrice()}</h3>
        </div>
      ) : (
        <p>No items in cart</p>
      )}
    </div>
  );
};

export default CartPage;
