import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import MenuImg from "./images/menu.png";
import { UserContext } from "./UserContext";

const NavBar = () => {
  const { user, cartItems } = useContext(UserContext);
  const [navbar, setNavbar] = useState(false);
  const [itemAdded, setItemAdded] = useState(false);

  useEffect(() => {
    const changeBg = () => {
      if (window.scrollY >= 100) {
        setNavbar(true);
      } else {
        setNavbar(false);
      }
    };

    window.addEventListener("scroll", changeBg);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener("scroll", changeBg);
    };
  }, []);

  useEffect(() => {
    if (itemAdded) {
      setTimeout(() => {
        setItemAdded(false);
      }, 3000); // Reset the notification after 3 seconds
    }
  }, [itemAdded]);

  const cartItemCount = cartItems ? cartItems.length : 0;

  const addToCart = () => {
    // Implement your logic to add an item to the cart
    // Set the `itemAdded` state to true to display the notification
    setItemAdded(true);
  };

  return (
    <>
      <nav
        className={
          navbar
            ? "navbar navbar-expand-lg navbar-expand-md fixed-top active"
            : "navbar navbar-expand-lg navbar-expand-md fixed-top"
        }
      >
        <a href="/" className="navbar-brand">
          <span>eco</span>Taran
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <img src={MenuImg} alt="menu" />
        </button>
        <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
          <div className="navbar-nav">
            <div className="nav-item">
              <a href="/" className="nav-link">
                Acasa
              </a>
            </div>
            <div className="nav-item">
              <a href="/IndexPage" className="nav-link">
                Anunturi
              </a>
            </div>
            {user ? (
              <div className="nav-item">
                <a href="/userpage" className="nav-link">
                  {user.name}
                </a>
              </div>
            ) : (
              <>
                <div className="nav-item">
                  <a href="/CartPage" className="nav-link">
                    Cart {cartItemCount > 0 && <span>({cartItemCount})</span>}
                  </a>
                </div>
                <div className="nav-item">
                  <a href="/login" className="nav-link">
                    Log In
                  </a>
                </div>
                <div className="nav-item">
                  <button className="add-to-cart" onClick={addToCart}>
                    Add to Cart
                  </button>
                  {itemAdded && <span className="notification">Item added to cart!</span>}
                </div>
              </>
            )}
          </div>
        </div>
      </nav>
    </>
  );
};

export default NavBar;
