import React, { useState, useEffect } from 'react';
import axios from 'axios';

import './OrdersPage.css';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios
      .get('/orders')
      .then((response) => {
        setOrders(response.data);
      })
      .catch((error) => {
        console.error('Failed to retrieve orders: ', error);
      });
  }, []);
  
  const handleClickDelivered = async (orderId) => {
    try {
      await axios.put(`/orders/${orderId}/markDelivered`);
      // Refresh the orders list by making another GET request
      const response = await axios.get('/orders');
      setOrders(response.data);
    } catch (error) {
      console.error('Failed to mark order as delivered: ', error);
    }
  };


  const handlePlaceOrder = async () => {
  try {
    // Place the order logic goes here
    // ...

    // Add the 'delivered' property to the new order
    const newOrder = { ...order, delivered: false }; // Assuming initially the order is not delivered
    setOrders([...orders, newOrder]);
  } catch (error) {
    console.error('Failed to place the order: ', error);
  }
};

if (!orders || orders.length === 0) {
  return <p>No orders found.</p>;
}

  return (
    <div className="orders-container">
    <h1>Orders</h1>
    {orders.map((order, index) => (
      <div
        key={index}
        className={`order ${order.delivered ? 'order-delivered' : ''}`}
      >
        <h3>Order #{index + 1}</h3>
        <div className="delivery-details">
          <strong>Delivery Details:</strong>
          <p>First Name: {order.firstName}</p>
          <p>Last Name: {order.lastName}</p>
          <p>Email: {order.email}</p>
          <p>Address: {order.address}</p>
          <p>City: {order.city}</p>
          <p>ZIP Code: {order.zipCode}</p>
        </div>
        <div className="cart-places">
          <strong>Cart Places:</strong>
          {order.cartItems && order.cartItems.length > 0 ? (
            order.cartItems.map((place, placeIndex) => (
              <div key={placeIndex} className="place">
                <p className="place-title">
                  {place.title} - Lei{place.km}
                </p>
                <p className="place-description">{place.description}</p>
                <p className="place-price">{place.price}</p>
                {/* Add more fields as needed */}
              </div>
            ))
          ) : (
            <p>No places found for this order.</p>
          )}
        </div>
        <button
          className="mark-delivered-btn"
          onClick={() => handleClickDelivered(order._id)}
          disabled={order.delivered}
        >
          {order.delivered ? 'Delivered' : 'Mark as Delivered'}
        </button>
      </div>
    ))}
    <button className="place-order-btn" onClick={handlePlaceOrder}>
      Place Order
    </button>
  </div>
);
};

export default OrdersPage;
