import React, { useState } from 'react';



const OrdersComponent = () => {
  const [orders, setOrders] = useState([]);

  const placeOrder = (formData, cart) => {
    const newOrder = {
      id: Date.now(), // Generate a unique ID for the order
      formData: { ...formData },
      cart: [...cart],
    };

    setOrders((prevOrders) => [...prevOrders, newOrder]);
  };


  
  return (
    <div>
      <h1>All Orders</h1>
      {orders.length > 0 ? (
        <ul>
          {orders.map((order) => (
            <li key={order.id}>
              <h2>Order ID: {order.id}</h2>
              <p>Customer Name: {order.formData.firstName} {order.formData.lastName}</p>
              <p>Email: {order.formData.email}</p>
              <p>Address: {order.formData.address}, {order.formData.city}, {order.formData.zipCode}</p>
              <p>Places in Cart:</p>
              <ul>
                {order.cart.map((place) => (
                  <li key={place.id}>{place.title}</li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      ) : (
        <p>No orders found.</p>
      )}

      {/* Render the checkout form and pass the placeOrder function as a prop */}
      <CheckoutForm placeOrder={placeOrder} />
    </div>
  );
};

const CheckoutForm = ({ placeOrder }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    address: '',
    city: '',
    zipCode: '',
  });
  const [cart, setCart] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call the placeOrder function and pass the form data and cart as arguments
    placeOrder(formData, cart);
    // Reset form data and cart
    setFormData({
      firstName: '',
      lastName: '',
      email: '',
      address: '',
      city: '',
      zipCode: '',
    });
    setCart([]);
  };

  const addToCart = (place) => {
    setCart((prevCart) => [...prevCart, place]);
  };

  return (
    <div>
      <h2>Checkout</h2>
      <form onSubmit={handleSubmit}>
        {/* Render the form inputs */}
        {/* ... */}
        <button type="submit">Place Order</button>
      </form>

      <h3>Cart</h3>
      {cart.length > 0 ? (
        <ul>
          {cart.map((place) => (
            <li key={place.id}>{place.title}</li>
          ))}
        </ul>
      ) : (
        <p>Your cart is empty.</p>
      )}

      {/* Render the component where the user can add places to the cart */}
      <PlaceListComponent addToCart={addToCart} />
    </div>
  );
};

const PlaceListComponent = ({ addToCart }) => {
  // Render the component where the user can select and add places to the cart
  // ...
};

export default OrdersComponent;
