import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PlaceSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    fetchPlaces();
  }, []);

  const fetchPlaces = async () => {
    try {
      // Fetch places from your server-side API endpoint using Axios
      const response = await axios.get('/places');
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error fetching places:', error);
    }
  };

  const handleSearch = () => {
    if (searchTerm === '') {
      setSearchResults([]);
      return;
    }

    const filteredPlaces = searchResults.filter(place =>
      place.title.toLowerCase().startsWith(searchTerm.toLowerCase().slice(0, 3))
    );
    setSearchResults(filteredPlaces);
  };

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
    handleSearch();
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search by title..."
        value={searchTerm}
        onChange={handleChange}
      />

      <ul>
        {searchResults.map(place => (
          <li key={place._id}>{place.title}</li>
        ))}
      </ul>
    </div>
  );
};

export default PlaceSearch;
