import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import cos from "./images/cos.png";

const CartLink = ({ cartQuantity }) => {
  const [refreshCart, setRefreshCart] = useState(false);

  useEffect(() => {
    if (cartQuantity > 0) {
      // Set refreshCart to true when cartQuantity changes
      setRefreshCart(true);

      // Reset refreshCart after a short delay
      const timer = setTimeout(() => {
        setRefreshCart(false);
      }, 1000);

      // Clear the timeout if the component unmounts
      return () => clearTimeout(timer);
    }
  }, [cartQuantity]);

  return (
    <div className="navbar-left">
      <Link to="/CartPage" className="nav-link">
        <img src={cos} alt="cos" />
        <span className={`cart-quantity ${refreshCart ? "refresh" : ""}`}>
          {cartQuantity}
        </span>
      </Link>
    </div>
  );
};

export default CartLink;
