import React, { useState, useEffect } from 'react';
import "./cart.css";

const CartPage = ({ cart }) => {
  const [cartItems, setCartItems] = useState([]);

  // Function to remove an item from the cart
  const removeFromCart = (placeId) => {
    const updatedCartItems = cartItems.filter((place) => place.id !== placeId);
    setCartItems(updatedCartItems);
  };

  // Function to calculate the total price of the cart
  const calculateTotalPrice = () => {
    return cartItems.reduce((total, place) => total + place.price, 0);
  };

  // Update cartItems when cart prop changes
  useEffect(() => {
    setCartItems(cart);
  }, [cart]);

  // Update cartItems when cartItems prop changes
  useEffect(() => {
    setCartItems(cart);
  }, [cartItems]);

  return (
    <div className="cart-page">
      <h1 className="cart-title">Shopping Cart</h1>
      {cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty.</p>
      ) : (
        <div className="cart-items-container">
          {cartItems.map((place) => (
            <div key={place.id} className="cart-item">
              <h3 className="place-name">{place.name}</h3>
              <p className="place-price">Price: ${place.price}</p>
              <button onClick={() => removeFromCart(place.id)} className="remove-button">
                Remove from Cart
              </button>
            </div>
          ))}
          <h3 className="cart-total">Total: ${calculateTotalPrice()}</h3>
          <button className="checkout-button">Checkout</button>
        </div>
      )}
    </div>
  );
};

export default CartPage;
