import React, { useEffect, useState, useContext, useRef } from "react";
import axios from "axios";
import "./IndexPage.css";
import { UserContext } from "./UserContext";
import { CartContext } from "./CartContext";
import CartLink from "./CartLink";
import { Link } from "react-router-dom";

function PlacePage() {
  const { cart, setCart } = useContext(UserContext);
  const { updateCartQuantity } = useContext(CartContext);
  const cartLinkRef = useRef(null);

  const [place, setPlace] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loadingPlaceId, setLoadingPlaceId] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    axios.get("/places").then((response) => {
      const filteredPlaces = response.data.filter(
        (place) => place.marca === "Fructe"
      );
      setPlaces(filteredPlaces);
    });
  }, []);

  const addToCart = () => {
    setLoadingPlaceId(place.id);
    setLoading(true);

    const updatedPlace = { ...place, quantity }; // Use the quantity state directly
    let updatedCart = localStorage.getItem("cart");
    if (!updatedCart) {
      updatedCart = [];
    } else {
      updatedCart = JSON.parse(updatedCart);
    }

    const placeIndex = updatedCart.findIndex((item) => item.id === place.id);
    if (placeIndex !== -1) {
      updatedCart[placeIndex].quantity += quantity;
    } else {
      updatedCart.push(updatedPlace);
    }

    localStorage.setItem("cart", JSON.stringify(updatedCart));
    setCart(updatedCart);

    // Call updateCartQuantity with the new cart quantity
    updateCartQuantity(updatedCart.length);

    setTimeout(() => {
      setLoading(false);
      setLoadingPlaceId(null);
    }, 1000);
  };

  const handleDecreaseQuantity = () => {
    if (quantity > 1) {
      setQuantity((prevQuantity) => prevQuantity - 1);
    }
  };

  const handleIncreaseQuantity = () => {
    setQuantity((prevQuantity) => prevQuantity + 1);
  };

  useEffect(() => {
    if (cartLinkRef.current) {
      cartLinkRef.current.forceUpdate();
    }
  }, [cart]);

  return (
    <>
      <div className="top"></div>
      <div className="main2">
        <div className="container">
          <div className="details container">
            <div className="row row-cols-sm-1 row-cols-md-2 row-cols-lg-3 g-4">
              {/* Map over places and render place cards */}
              {currentPlaces.length > 0 &&
                currentPlaces.map((place) => (
                  <div className="col" key={place._id}>
                    <div className="box card-body p-0 shadow-sm mb-5">
                      <Link
                        to={"/place/" + place._id}
                        key={place._id}
                        className="link-no-underline"
                      >
                        {place.photos.length > 0 && (
                          <img
                            src={place.photos[0]}
                            className="img-fluid"
                            alt={place.title}
                            style={{
                              height: "270px",
                              width: "100%",
                              objectFit: "cover",
                            }}
                          />
                        )}
                      </Link>
                      <div className="box_content">
                        <h4>{place.title}</h4>
                        <div className="row pl-2 pr-2">
                          {/* Quantity control */}
                          <div className="quantity-control">
                            <div
                              className="quantity-btn-container"
                              style={{
                                display: "flex",
                                flexWrap: "wrap",
                                alignContent: "flex-end",
                                justifyContent: "space-between",
                                background: "#d3d3d3",
                                borderRadius: "10%",
                                alignItems: "center",
                              }}
                            >
                              <button
                                className="quantity-btn"
                                style={{
                                  backgroundColor: "rgb(154 154 154)",
                                  color: "white",
                                  padding: "5px",
                                  border: "none",
                                  borderRadius: "10%",
                                  cursor: "pointer",
                                  marginRight: "5px",
                                }}
                                onClick={handleDecreaseQuantity}
                              >
                                -
                              </button>
                              <div className="quantity">{quantity}</div>
                              <button
                                className="quantity-btn"
                                style={{
                                  backgroundColor: "rgb(154 154 154)",
                                  color: "white",
                                  padding: "5px",
                                  border: "none",
                                  borderRadius: "10%",
                                  cursor: "pointer",
                                  marginLeft: "5px",
                                }}
                                onClick={handleIncreaseQuantity}
                              >
                                +
                              </button>
                            </div>
                            <div
                              style={{
                                display: "flex",
                                flexWrap: "wrap",
                                alignContent: "flex-end",
                                justifyContent: "space-around",
                                alignItems: "center",
                              }}
                            >
                              {place.km} lei
                            </div>

                            {/* Add to cart button */}
                            <button
                              style={{
                                padding: "4px",
                                width: "300px",
                              }}
                              className="btn1"
                              onClick={addToCart}
                            >
                              {loadingPlaceId === place.id ? (
                                <div className="loading-animation">
                                  <div className="spinner"></div>
                                </div>
                              ) : (
                                <span>Adauga in cos</span>
                              )}
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default PlacePage;
