import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Image from './image';

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);

  const removeFromCart = (title) => {
    const updatedCartItems = cartItems.filter((place) => place.title !== title);
    setCartItems(updatedCartItems);
    localStorage.setItem('cart', JSON.stringify(updatedCartItems));
  };

  const increaseQuantity = (title) => {
    const updatedCartItems = cartItems.map((place) => {
      if (place.title === title) {
        return { ...place, quantity: place.quantity + 1 };
      }
      return place;
    });
    setCartItems(updatedCartItems);
    localStorage.setItem('cart', JSON.stringify(updatedCartItems));
  };

  const calculateTotalKm = () => {
    return cartItems.reduce((total, place) => total + place.km * place.quantity, 0);
  };

  useEffect(() => {
    const cartData = localStorage.getItem('cart');
    if (cartData) {
      setCartItems(JSON.parse(cartData));
    }
  }, []);

  useEffect(() => {
    const updatedCartQuantity = cartItems.reduce((total, place) => total + place.quantity, 0);
    localStorage.setItem('cart', JSON.stringify(cartItems));
    // Update the cart quantity in the navigation bar (if available)
    const navCartQuantity = document.getElementById('nav-cart-quantity');
    if (navCartQuantity) {
      navCartQuantity.innerText = updatedCartQuantity.toString();
    }
  }, [cartItems]);

  return (
    <div style={{ padding: '260px' }}>
      <h1 style={headerStyle}>Cos de</h1>
      {cartItems.length === 0 ? (
        <p style={emptyCartStyle}>Your cart is empty.</p>
      ) : (
        <div>
          {cartItems.map((place) => (
            <div key={place.id} style={cartItemStyle}>
              <h3>{place.title}</h3>
              {place.photos.length > 0 && (
                <Image
                  src={place.photos[0]}
                  alt={place.title}
                  className="img-fluid"
                  style={{
                    height: '100px',
                    width: '30%',
                    objectFit: 'cover',
                  }}
                />
              )}
              <p style={priceStyle}>Price: Lei {place.km}</p>
              <div style={quantityContainerStyle}>
                <button style={quantityButtonStyle} onClick={() => increaseQuantity(place.title)}>
                  +
                </button>
                <span style={quantityStyle}>{place.quantity}</span>
                <button
                  style={quantityButtonStyle}
                  onClick={() => {
                    if (place.quantity > 1) {
                      setCartItems((prevItems) =>
                        prevItems.map((prevItem) =>
                          prevItem.title === place.title ? { ...prevItem, quantity: prevItem.quantity - 1 } : prevItem
                        )
                      );
                    } else {
                      removeFromCart(place.title);
                    }
                  }}
                >
                  -
                </button>
              </div>
              <button style={removeButtonStyle} onClick={() => removeFromCart(place.title)}>
                Remove from Cart
              </button>
            </div>
          ))}
          <h3 style={totalStyle}>Total: {calculateTotalKm()} Lei</h3>
          <Link to="/checkout">
            <button style={checkoutButtonStyle}>Checkout</button>
          </Link>
        </div>
      )}
    </div>
  );
};

// Styles

const headerStyle = {
  fontSize: '3rem',
  textAlign: 'center',
  marginBottom: '2rem',
};

const emptyCartStyle = {
  fontSize: '1.5rem',
  textAlign: 'center',
};

const cartItemStyle = {
  marginBottom: '20px',
  borderBottom: '1px solid #ccc',
  paddingBottom: '10px',
  };
  
  const priceStyle = {
  fontSize: '1.2rem',
  marginBottom: '10px',
  };
  
  const removeButtonStyle = {
  backgroundColor: '#ff5252',
  color: 'white',
  padding: '10px 20px',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  };
  
  const totalStyle = {
  marginTop: '2rem',
  fontSize: '2rem',
  fontWeight: 'bold',
  };
  
  const checkoutButtonStyle = {
  backgroundColor: '#4CAF50',
  color: 'white',
  padding: '10px 20px',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  marginTop: '2rem',
  display: 'block',
  margin: '0 auto',
  };
  
  const quantityContainerStyle = {
  display: 'flex',
  alignItems: 'center',
  };
  
  const quantityButtonStyle = {
  backgroundColor: '#cccccc',
  color: 'white',
  padding: '5px',
  border: 'none',
  borderRadius: '50%',
  cursor: 'pointer',
  marginRight: '5px',
  };
  
  const quantityStyle = {
  fontSize: '1.2rem',
  fontWeight: 'bold',
  };
  
  export default CartPage;
