import React, { useState } from 'react';

const Checkout = ({ cartPlaces, clearCart }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleAddressChange = (e) => {
    setAddress(e.target.value);
  };

  const handleCheckout = () => {
    // Perform the checkout process
    // Send the cart places, name, email, and address to the server
    // Handle the server response and show a success message or error

    // Clear the cart after successful checkout
    clearCart();
  };

  return (
    <div>
      <h2>Checkout</h2>
      <form>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={handleNameChange} />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={handleEmailChange} />
        </div>
        <div>
          <label>Address:</label>
          <textarea value={address} onChange={handleAddressChange} />
        </div>
        <button onClick={handleCheckout}>Checkout</button>
      </form>
    </div>
  );
};

export default Checkout;
