import React, { useState, useEffect } from 'react';
import Image from './image';

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);

  // Function to remove an item from the cart
  const removeFromCart = (placeId) => {
    const updatedCartItems = cartItems.filter((place) => place.id !== placeId);
    setCartItems(updatedCartItems);
  };

  // Function to calculate the total price of the cart
  const calculateTotalPutere = () => {
    return cartItems.reduce((total, place) => total + place.putere, 0);
  };

  useEffect(() => {
    const cartData = localStorage.getItem('cart');
    if (cartData) {
      setCartItems(JSON.parse(cartData));
    }
  }, []);

  return (
    <div>
      <h1>Shopping Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cartItems.map((place) => (
            <div key={place.id} style={cartItemStyle}>
              <h3>{place.title}</h3>
              {place.photos.length > 0 && (
                <Image
                  src={place.photos[0]}
                  alt={place.title}
                  className="img-fluid"
                  style={{
                    height: '270px',
                    width: '30%',
                    objectFit: 'cover',
                  }}
                />
              )}
              <p>Price: ${place.putere}</p>
              <button onClick={() => removeFromCart(place.id)}>
                Remove from Cart
              </button>
            </div>
          ))}
          <h3>Total: ${calculateTotalPutere()}</h3>
          <button style={checkoutButtonStyle}>Checkout</button>
        </div>
      )}
    </div>
  );
};

const cartItemStyle = {
  marginBottom: '20px',
  borderBottom: '1px solid #ccc',
  paddingBottom: '10px',
};

const checkoutButtonStyle = {
  backgroundColor: '#4CAF50',
  color: 'white',
  padding: '10px 20px',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
};

export default CartPage;
