function Categorii() {


    const resetFilters = () => {
        setSelectedMarca("");
        setSelectedModel("");
        setSelectedAnul("");
        setSelectedCombustibil("");
        setSelectedPutere("");
        setSelectedKm("");
        setSelectedKmMin("");
        setSelectedKmMax("");
        setSelectedTitleMin("");
        setSelectedTitleMax("");
      };
      
  const handleMarcaClick = (marca) => {
    setSelectedMarca(selectedMarca === marca ? '' : marca);
  };


  return (
    <>
      <div className="container">
        <div className="filter-container">
        

        <div className="marca-buttons">
        <button
        onClick={() => handleMarcaClick('Fructe')}
        className={`marca-button ${selectedMarca === 'Fructe' ? 'active' : ''}`}
      >
        Fructe
       
      </button>



  <button
    onClick={() => setSelectedMarca('Legume')}
    className={`marca-button ${selectedMarca === 'Legume' ? 'active' : ''}`}
  >
    Legume
  </button>
  


  <button
    onClick={() => setSelectedMarca('Mezeluri')}
    className={`marca-button ${selectedMarca === 'Mezeluri' ? 'active' : ''}`}
  >
    Mezeluri
  </button>
  


  <button
    onClick={() => setSelectedMarca('Lactate')}
    className={`marca-button ${selectedMarca === 'Lactate' ? 'active' : ''}`}
  >
    Lactate
  </button>
  
</div>


          <div className="filter-item">
          <label htmlFor="model-select">Tipuri</label>
      <select id="model-select" value={selectedModel} onChange={handleModelSelect}>
        <option value="">Toate</option>
        {filteredPlaces.map(place => (
          <option key={place.id} value={place.model}>{place.model}</option>
        ))}
      </select>
    </div>
          



         
          <div className="filter-item">
        <button onClick={resetFilters}>Reset</button>
      </div>
    </div>
    </div>
    </>
  );
}

export default Categorii;
