import React, { useState } from 'react';
import './CheckoutPage.css';

const CheckoutPage = () => {
  const [items, setItems] = useState([
    { id: 1, name: 'Apple', price: 1.5 },
    { id: 2, name: 'Banana', price: 0.75 },
    { id: 3, name: 'Orange', price: 0.9 },
    // Add more items here
  ]);

  const [cart, setCart] = useState([]);

  const addToCart = (item) => {
    setCart([...cart, item]);
  };

  const removeFromCart = (item) => {
    const updatedCart = cart.filter((cartItem) => cartItem.id !== item.id);
    setCart(updatedCart);
  };

  const calculateTotalPrice = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price;
    });
    return total.toFixed(2); // Limit to 2 decimal places
  };

  return (
    <div className="checkout-page">
      <h2 className="checkout-title">Checkout Page</h2>
      <div className="cart-section">
        <h3 className="section-title">Items in Cart:</h3>
        <ul className="cart-items">
          {cart.map((item) => (
            <li key={item.id} className="cart-item">
              <div className="item-info">
                <span className="item-name">{item.name}</span>
                <span className="item-price">${item.price.toFixed(2)}</span>
              </div>
              <button className="remove-btn" onClick={() => removeFromCart(item)}>
                Remove
              </button>
            </li>
          ))}
        </ul>
        <h3 className="total-price">Total Price: ${calculateTotalPrice()}</h3>
      </div>
      <div className="items-section">
        <h3 className="section-title">Available Items:</h3>
        <ul className="available-items">
          {items.map((item) => (
            <li key={item.id} className="available-item">
              <div className="item-info">
                <span className="item-name">{item.name}</span>
                <span className="item-price">${item.price.toFixed(2)}</span>
              </div>
              <button className="add-btn" onClick={() => addToCart(item)}>
                Add to Cart
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default CheckoutPage;
