import React, { useState, useEffect, useRef } from 'react';
import Header from "./header";
import About2 from "./about2";
import Details from "./details";
import About from "./about";
import Details2 from "./details2";
import Header2 from "./header2";
import CookiePolicyPopup from "./CookiePolicyPopup";
import IndexPage from "./IndexPage";
import SplashScreen from "./SplashScreen";
import Popup from "./Popup";
import Categorii from "./categorii";
import Footer2 from "./footer2";
import './home.css'; // import the fade-in class

function Home() {
  const [showPopup, setShowPopup] = useState(false);
  const handleScroll = () => {
    if (window.pageYOffset > 1600) {
      setShowPopup(true);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Add a ref to the component
  const ref = useRef(null);

  // Use the IntersectionObserver API to check if the component is in the viewport
  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      if (entry.isIntersecting) {
        entry.target.classList.add('fade-in'); // Add the fade-in class
        observer.unobserve(entry.target);
      }
    }, { threshold: 0.1 });

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      observer.disconnect();
    };
  }, []);

  return (
    <div>
      {showPopup && <Popup />}
      <Header/>
      <About2 ref={ref} className="fade-in" />
<Categorii ref={ref} className="fade-in" />
<About ref={ref} className="fade-in" /> 
<Details2 ref={ref} className="fade-in" />
<Header2 ref={ref} className="fade-in" />
<Footer2 ref={ref} className="fade-in" />
    </div>
  );
};

export default Home;
