import React, { useState, useEffect } from 'react';

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);

  // Function to remove an item from the cart
  const removeFromCart = (placeId) => {
    const updatedCartItems = cartItems.filter((place) => place.id !== placeId);
    setCartItems(updatedCartItems);
  };

  // Function to calculate the total price of the cart
  const calculateTotalPrice = () => {
    return cartItems.reduce((total, place) => total + place.price, 0);
  };

  useEffect(() => {
    const cartData = localStorage.getItem('cart');
    if (cartData) {
      setCartItems(JSON.parse(cartData));
    }
  }, []);

  return (
    <div>
      <h1>Shopping Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cartItems.map((place) => (
            <div key={place.id}>
              <h3>{place.name}</h3>
              <p>Price: ${place.putere}</p>
              <button onClick={() => removeFromCart(place.id)}>
                Remove from Cart
              </button>
            </div>
          ))}
          <h3>Total: ${calculateTotalPrice()}</h3>
          <button>Checkout</button>
        </div>
      )}
    </div>
  );
};

export default CartPage;
